00424 bool XSERIALIZATIONMETHODJSON::AddStruct(XCHAR* name, bool open)
00425 {
00426   if(!CheckHandleActive()) 
00427     {
00428       return false;
00429     }
00430 
00431   if(open)
00432     {
00433       XFILEJSONOBJECT* structnode =  new XFILEJSONOBJECT();
00434       if(!structnode)
00435         {
00436           return false;
00437         }
00438 
00439       fathers.Add(GetActualObject());      
00440       SetActualObject(structnode);
00441     }
00442    else
00443     {
00444       fathers.GetLast()->Add(name, (XFILEJSONOBJECT*)GetActualObject());  
00445       SetActualObject(fathers.GetLast());      
00446       fathers.DeleteLast();
00447     }
00448 
00449   return true;
00450 }
