01288 bool XSTRING::DeleteCharacters(XDWORD index, XDWORD ncharacteres)
01289 {
01290   if(!ncharacteres)  return true;
01291   if(IsEmpty())      return false;
01292 
01293   if(index >= size)  return false;
01294 
01295   int nchar = ncharacteres;
01296   if((index + nchar) > size) nchar = (int)size-index;
01297 
01298   int c = index;
01299   while(text[c+nchar])
01300     {
01301       text[c] = text[c+nchar];
01302       c++;
01303     }
01304   text[c] = text[c + nchar];
01305 
01306   return ReAllocBuffer(size - nchar);
01307 }
