01353 bool XSTRING::DeleteCharacter(XCHAR character,XSTRINGCONTEXT context)
01354 {
01355   if(IsEmpty()) return false;
01356 
01357   switch(context)
01358     {
01359       case XSTRINGCONTEXT_FROM_FIRST  : { XDWORD n=0;
01360                                           while(character == text[n] && n<size)
01361                                             {
01362                                               n++;
01363                                             }
01364                                           if(n) DeleteCharacters(0,n);
01365                                         }
01366                                         break;
01367 
01368       case XSTRINGCONTEXT_TO_END      : { XDWORD n=size-1;
01369                                           while(character == text[n] && n)
01370                                             {
01371                                               n--;
01372                                             }
01373 
01374                                           if(size!=n+1)  ReAllocBuffer(n+1);
01375                                         }
01376                                         break;
01377 
01378       case XSTRINGCONTEXT_ALLSTRING   : { XDWORD c = 0;
01379                                           XDWORD a = 0;
01380 
01381                                           while (c < size)
01382                                             {
01383                                               while(text[c]==character)
01384                                                 {
01385                                                   c++;
01386                                                 }
01387 
01388                                               text[a] = text[c];
01389                                               c++;
01390 
01391                                               if(!text[a]) break;
01392                                               a++;
01393                                             }
01394 
01395                                           ReAllocBuffer(a);
01396                                           size = a;
01397                                         }
01398                                         break;
01399     }
01400 
01401   return true;
01402 }
