01560   if(index <= 0) 
01561     {
01562       return false;
01563     }
01564 
01565   while(Character_GetLast () == __C('0'))
01566     {
01567       DeleteLastCharacter();
01568       status = true;
01569     }
01570 
01571   return status;
01572 }
01573 
01574 
01586 bool XSTRING::Character_IsAlpha(XCHAR character) const
01587 {
01588   XBYTE _char = (XBYTE)character;
01589 
01590   if(((_char>='a') && (_char<='z')) ||
01591      ((_char>='A') && (_char<='Z'))) return true;
01592 
01593   return false;
01594 }
