01638 
01650 bool XSTRING::Character_IsNumber(XCHAR character)
01651 {  
01652   if((character >= __C('0')) && (character <= '9'))  return true;
01653 
01654   if(character == __C('.')) return true;
01655   if(character == __C(',')) return true;
01656   if(character == __C('-')) return true;
01657   if(character == __C('E')) return true;
01658   if(character == __C('+')) return true;
01659 
01660   return false;
01661 }
