01663 
01675 bool XSTRING::Character_IsNOT(XCHAR character)
01676 {
01677   switch(character)
01678     {
01679       case 0x0000: return true;
01680       case 0x0009: return true; // Tab
01681       case 0x000A: return true; // Return
01682       case 0x000D: return true; // Return
01683       case 0x0020: return true; // Space
01684     }
01685 
01686   return false;
01687 }
