01689 
01701 XCHAR XSTRING::Character_ToUpper(XCHAR character) const
01702 {
01703 
01704   XCHAR _character = character;
01705   XBYTE _char      = (XBYTE)_character;
01706 
01707   if((_char>='a')&&(_char<='z'))
01708     {
01709        _char      -= 32;
01710        _character  = (XCHAR)_char;
01711     }
01712 
01713   return _character;
01714 }
01715 
01716 
01728 XCHAR XSTRING::Character_ToLower(XCHAR character) const
01729 {
01730   XCHAR _character = character;
01731   XBYTE _char      = (XBYTE)_character;
01732 
01733   if((_char>='A')&&(_char<='Z'))
01734     {
01735        _char      += 32;
01736        _character  = (XCHAR)_char;
01737     }
01738 
01739   return _character;
01740 }
