01759 
01769 XCHAR XSTRING::Character_GetLast()
01770 {
01771   int c;
01772 
01773   if(IsEmpty()) return 0;
01774 
01775   for(c=0;c<(int)size;c++)
01776     {
01777       if(!text[c]) break;
01778     }
01779 
01780   if((c-1) >=0) return text[c-1];
01781 
01782   return 0;
01783 }
