01850 bool XSTRING::IsDecimalNumber(int* ndecimals)
01851 {
01852   if(ndecimals)
01853     {
01854       (*ndecimals) = 0;
01855     }
01856 
01857   if(IsEmpty()) 
01858     {
01859       return false;
01860     }
01861 
01862   if(!IsNumber())
01863     {
01864       return false;
01865     }
01866   
01867   bool found = false;
01868 
01869   for(XDWORD c=0;c<size;c++)
01870     {      
01871       if((text[c] == __C('.')) || (text[c] == __C(',')))
01872         {          
01873           (*ndecimals) = (size - c);
01874 
01875           found = true;
01876           break;
01877         }
01878     }
01879 
01880   return found;
01881 }
