01935 
01949 int XSTRING::Compare(const XCHAR* string, int csize, bool ignorecase)
01950 {
01951   XDWORD stringsize=csize;
01952 
01953   if(size!=stringsize)
01954     {
01955       if(size>stringsize) return  1;
01956       if(size<stringsize) return -1;
01957     }
01958 
01959   if(!size && !stringsize) return 0;
01960 
01961   XCHAR* _text = (XCHAR*)string;
01962 
01963   if(ignorecase)
01964     {
01965       for(int c=size-1;c>=0;c--)
01966         {
01967           XCHAR a=_text[c];
01968           XCHAR b=text[c];
01969 
01970           if(Character_IsAlpha(a))
01971             {
01972               XCHAR c1 = Character_ToUpper(a);
01973               XCHAR c2 = Character_ToUpper(b);
01974 
01975               if(c1 == c2) continue;
01976               if(c1 < c2) return  1;
01977               if(c1 > c2) return -1;
01978             }
01979            else
01980             {
01981               if(a == b)  continue;
01982               if(a < b)   return  1;
01983               if(a > b)   return -1;
01984             }
01985         }
01986     }
01987    else
01988     {
01989       for(int c=size-1;c>=0;c--)
01990         {
01991           if(_text[c] == text[c]) continue;
01992           if(_text[c] < text[c])  return  1;
01993           if(_text[c] > text[c])  return -1;
01994         }
01995     }
01996 
01997   return 0;
01998 }
