02189 
02203 int XSTRING::Find(XSTRING& string, bool ignorecase,int startindex)
02204 {
02205   if(IsEmpty())                      return XSTRING_NOTFOUND;
02206   if(string.IsEmpty())               return XSTRING_NOTFOUND;
02207 
02208   XCHAR* _text = string.Get();
02209   XDWORD _size = string.GetSize();
02210 
02211   if(_size > size)                    return XSTRING_NOTFOUND;
02212   if(startindex>=(int)(size-_size)+1) return XSTRING_NOTFOUND;
02213   if(startindex<0)                    return XSTRING_NOTFOUND;
02214 
02215   bool found;
02216 
02217   for(XDWORD c=startindex;c<(size-_size)+1;c++)
02218     {
02219       found = true;
02220 
02221       for(XDWORD d=0;d<_size;d++)
02222         {
02223           if(ignorecase && Character_IsAlpha(_text[d]))
02224             {
02225               XCHAR c1 = Character_ToUpper(_text[d]);
02226               XCHAR c2 = Character_ToUpper(text[c+d]);
02227 
02228               if(c1!=c2)
02229                 {
02230                   found = false;
02231                   break;
02232                 }
02233             }
02234            else
02235             {
02236               if(_text[d]!=text[c+d])
02237                 {
02238                   found = false;
02239                   break;
02240                 }
02241             }
02242         }
02243 
02244       if(found) return c;
02245     }
02246 
02247   return XSTRING_NOTFOUND;
02248 }
02249 
02250 
02264 int XSTRING::FindCharacter(XCHAR character, XDWORD startindex, bool endtostart)
02265 {
02266   if(startindex >= (XDWORD)size)  return XSTRING_NOTFOUND;
02267 
02268   if(endtostart)
02269     {
02270       for(int c=size-startindex-1; c>=0; c--)
02271         {
02272           if(text[c]==character) return c;
02273         }
02274     }
02275    else
02276     {
02277       for(XDWORD c=startindex; c<size; c++)
02278         {
02279           if(text[c] == character) return c;
02280         }
02281     }
02282 
02283   return XSTRING_NOTFOUND;
02284 }
