02305   if(startindex>=(int)size) return 0;
02306   if(startindex<0)          return 0;
02307 
02308   if(endtostart)
02309     {
02310       for(c=size-startindex; c>0; c--)
02311         {
02312           if(text[c]==character) count++;
02313         }
02314     }
02315    else
02316     {
02317       for(c=startindex; c<size; c++)
02318         {
02319           if(text[c]==character) count++;
02320         }
02321     }
02322 
02323   return count;
02324 }
