02401 bool XSTRING::AreValidCharacters(XCHAR* validcharacterslist)
02402 {
02403   if(!validcharacterslist) return false;
02404 
02405   XSTRING vcl;
02406 
02407   vcl = validcharacterslist;
02408 
02409   for(int d=0; d<(int)GetSize(); d++)
02410     {
02411       bool isvalid = false;
02412 
02413       for(int c=0; c<(int)vcl.GetSize(); c++)
02414         {
02415           if(Get()[d] == vcl.Get()[c])
02416             {
02417               isvalid = true;
02418               break;
