02444 int XSTRING::Copy(const XCHAR* startmark, const XCHAR* endmark, bool ignorecase, int addstartindex, XSTRING& string)
02445 {
02446   int startindex  = 0;
02447   int endindex    = GetSize();
02448   XDWORD sizeendmark = 0;
02449 
02450   string.Empty();
02451 
02452   if(startmark)
02453     {
02454       XSTRING mark;
02455 
02456       mark = startmark;
02457       startindex = Find(startmark, ignorecase, addstartindex);
02458       if(startindex==XSTRING_NOTFOUND) return XSTRING_NOTFOUND;
02459 
02460       startindex+= mark.GetSize();
02461     }
02462 
02463   if(endmark)
02464     {
02465       XSTRING mark;
02466 
02467       mark = endmark;
02468       endindex = Find(endmark, ignorecase, startindex);
02469       if(endindex==XSTRING_NOTFOUND) endindex = size;
02470 
02471       sizeendmark = mark.GetSize();
02472     }
02473 
02474   for(int c=startindex;c<endindex;c++)
02475     {
02476       XCHAR str[2] = { text[c], 0 };
02477 
02478       string+=str;
02479     }
02480 
02481   int indexnext = (endindex+sizeendmark);
02482 
02483   if(indexnext>(int)size) indexnext = (int)size;
02484 
02485   return indexnext;
02486 }
