02629 
02642 int XSTRING::Replace(XCHAR* findwhat, XCHAR* replaceby)
02643 {
02644   XSTRING what;
02645   XSTRING replace;
02646 
02647   what.Set(findwhat);
02648 
02649   replace.Set(replaceby);
02650 
02651   int found;
02652   int coincidences = 0;
02653 
02654   do{ found = ReplaceFirst(what.Get(),replace.Get());
02655 
02656       if (found!=XSTRING_NOTFOUND)
02657         coincidences++;
02658       else
02659         break;
02660     } while(1);
02661 
02662   return coincidences;
02663 }
02664 
02665 
02678 int XSTRING::ReplaceFirst(XCHAR* findwhat, XCHAR* replaceby)
02679 {
02680   XSTRING what;
02681   XSTRING replace;
02682 
02683   what.Set(findwhat);
02684   replace.Set(replaceby);
02685 
02686   int whatsize = what.GetSize();
02687   int found    = 0;
02688 
02689   found = Find(findwhat, false, found);
02690   if(found != XSTRING_NOTFOUND)
02691     {
02692       DeleteCharacters(found, whatsize);
02693       Insert(replace, found);
02694     }
02695 
02696   return found;
02697 }
02698 
02699 
02712 bool XSTRING::Insert(XSTRING str,XDWORD position)
02713 {
02714   XSTRING tmp;
02715 
02716   if(position > size)
02717     {
02718       return this->Add(str);
02719     }
02720 
02721   Copy(0,position,tmp);
02722 
02723   tmp.Add(str);
02724 
02725   XSTRING trail;
02726 
02727   Copy(position, trail);
02728   tmp.Add(trail);
02729 
02730   Set(tmp);
02731 
02732   return true;
02733 }
