02789 bool XSTRING::ConvertFromShort(short value, const XCHAR* mask)
02790 {
02791   char*     str;
02792   XSTRING  _mask;
02793 
02794   str = new char[XSTRING_MAXTEMPOSTR];
02795   if(!str) return false;
02796 
02797   memset(str,0,XSTRING_MAXTEMPOSTR);
02798 
02799   if(mask) _mask = mask; else _mask = __L("%hd");
02800 
02801   XBUFFER charstr;
02802   
02803   _mask.ConvertToASCII(charstr);
02804   SPRINTF(str, charstr.GetPtrChar(), value);
02805   
02806   Set(str);
02807 
02808   delete [] str;
02809 
02810   return true;
02811 }
