02867 
02868   str = new char[XSTRING_MAXTEMPOSTR];
02869   if(!str) return false;
02870 
02871   memset(str,0,XSTRING_MAXTEMPOSTR);
02872 
02873   if(mask) _mask = mask; else _mask = __L("%d");
02874 
02875 
02876   XBUFFER charstr;
02877   
02878   _mask.ConvertToASCII(charstr);
02879   SPRINTF(str, charstr.GetPtrChar(), value);
02880   
02881   Set(str);
02882 
02883   delete [] str;
02884 
02885   return true;
02886 }
