03083 
03093 bool XSTRING::ConvertHexFormatChars()
03094 {
03095   XSTRING string;
03096 
03097   string.Empty();
03098 
03099   if(IsEmpty()) return false;
03100 
03101   for(int c=0;c<(int)GetSize();c++)
03102     {
03103       XCHAR character[3] = { 0 , 0 , 0 };
03104 
03105       character[0] = Get()[c];
03106       character[1] = Get()[c+1];
03107 
03108       if((character[0]==__C('\\')) && (character[1]==__C('x')))
03109         {
03110           XSTRING   numberhex;
03111           XCHAR part[2] = { 0 , 0 };
03112 
03113           c+=2;
03114 
03115           for(int d=0;d<2;d++)
03116             {
03117               part[0] = Get()[c+d];
03118               numberhex.Add(part);
03119             }
03120 
03121           numberhex.UnFormat(__L("%02X"),&part[0]);
03122 
03123           part[0]&=0x00FF;
03124           string.Add(part);
03125 
03126           c++;
03127         }
03128        else string.Add((XCHAR)character[0]);
03129     }
03130 
03131   if(string.IsEmpty()) return false;
03132 
03133   Set(string);
03134 
03135   return true;
03136 }
03137 
03138 
03151 bool XSTRING::ConvertToHexString(XSTRING& string, bool uppercase)
03152 {
03153   XSTRING datastr;
03154 
03155   string.Empty();
03156 
03157   for(int c=0;c<(int)GetSize(); c++)
03158     {
03159       datastr.Format(uppercase?__L("%02X"):__L("%02x"), Get()[c]);
03160       string.Add(datastr);
03161     }
03162 
03163   return true;
03164 }
03165 
03166 
03178 bool XSTRING::ConvertHexStringToBuffer(XBUFFER& xbuffer)
03179 {
03180   xbuffer.Delete();
03181 
03182   for(int c=0; c<(int)GetSize(); c+=2)
03183     {
03184       XCHAR    hexbytechar[3] = { 0, 0, 0 };
03185       XSTRING  hexbytestr;
03186       int      data;
03187 
03188       hexbytechar[0] = Get()[c];
03189       hexbytechar[1] = Get()[c+1];
03190 
03191       hexbytestr = hexbytechar;
03192 
03193       hexbytestr.UnFormat(__L("%02X"), &data);
03194       xbuffer.Add((XBYTE)data);
03195     }
03196 
03197   return true;
03198 }
