03228 
03238 bool XSTRING::ConvertToBoolean()
03239 {
03240   bool result = false;
03241 
03242   if(!Compare(__L("yes")  , true)  || 
03243      !Compare(__L("true") , true)  || 
03244      !Compare(__L("YES")  , true)  || 
03245      !Compare(__L("TRUE") , true))
03246     {
03247       result = true;
03248     }
03249    else
03250     {
03251       if(!Compare(__L("no")    , true)    || 
03252          !Compare(__L("false") , true)    || 
03253          !Compare(__L("NO")    , true)    || 
03254          !Compare(__L("FALSE") , true))
03255         {
03256           result = false;
03257         }
03258        else
03259         {
03260           if(Compare(__L("0")  , true)) 
03261             {
03262               result = true;
03263             }
03264         }
03265     }
03266 
03267   return result;
03268 }
