03284 int XSTRING::ConvertToInt(int index, const XCHAR* mask, bool checkvalidchars)
03285 {
03286   if(IsEmpty()) return 0;
03287 
03288   if(checkvalidchars)
03289     {
03290       XSTRING  validcharacters;
03291       validcharacters  = XSTRING_VALIDCHARSINT;
03292       if(!AreValidCharacters(validcharacters.Get())) return 0;
03293     }
03294 
03295   XSTRING  _mask;
03296   int      data = 0;
03297 
03298   if(mask)
03299          _mask = mask;
03300     else _mask = __L("%d");
03301     
03302     
03303   XBUFFER charstr;
03304   XBUFFER charstr2;
03305   
03306   _mask.ConvertToASCII(charstr);     
03307   (*this).ConvertToASCII(charstr2);         
03308   
03309   SSCANF(&charstr2.GetPtrChar()[index], charstr.GetPtrChar(), &data);
03310  
03311   return data;
03312 }
