03529 bool XSTRING::IsValidASCII()
03530 {
03531   for(int c=0;c<(int)size;c++)
03532     {
03533       if((text[c]<0x20) || (text[c]>0x7F)) return false;
03534     }
03535 
03536   return true;
03537 }
03538 
03539 
03551 bool XSTRING::ConvertToASCII(XBUFFER& xbuffer)
03552 {   
03553   xbuffer.Empty();
03554 
03555   for(XDWORD c=0; c<size; c++)
03556     {    
03557       if(text[c]<0x80) 
03558         {                    
03559           xbuffer.Add((XBYTE)(text[c]));
03560         }
03561        else
03562         {
03563           xbuffer.Add((XBYTE)'_');
03564         } 
03565     }
03566 
03567   xbuffer.Add((XBYTE)0x00);
03568 
03569   return true;
03570 }
03571     
03572 
03584 bool XSTRING::ConvertFromASCII(XBUFFER& xbuffer)
03585 {
03586   if(xbuffer.IsEmpty()) return false;
03587 
03588   Empty();
03589 
03590   for(XDWORD c=0; c<xbuffer.GetSize()-1; c++)
03591     {
03592       XCHAR character = (XCHAR)(xbuffer.Get()[c]);
03593 
03594       Add((XCHAR)character);      
03595     }
03596   
03597   return true;
03598 }
