03600 
03612 bool XSTRING::ConvertToXBuffer(XBUFFER& xbuffer)
03613 {
03614   bool status = false;
03615 
03616   if(IsEmpty()) return size;
03617 
03618   xbuffer.Empty();
03619 
03620   switch(GetTypeCoding())
03621     {
03622       case XSTRINGCODING_UTF16  : { for(XDWORD c=0; c<size; c++)
03623                                       {
03624                                         xbuffer.Add((XWORD)text[c]);
03625                                       }
03626                                   
03627                                     status = true;  
03628                                   }
03629                                   break;  
03630 
03631       case XSTRINGCODING_UTF32  : { for(XDWORD c=0; c<size; c++)
03632                                       {
03633                                         xbuffer.Add((XDWORD)text[c]);
03634                                       }
03635                                   
03636                                     status = true;  
03637                                   }
03638                                   break;  
03639     }
03640 
03641   return status;
03642 }
03643 
03644 
03657 bool XSTRING::ConvertFromXBuffer(XBUFFER& xbuffer, XSTRINGCODING buffercoding)
03658 {  
03659   bool status = false;
03660 
03661   if(xbuffer.IsEmpty()) return false;
03662 
03663   Empty();
03664 
03665   switch(GetTypeCoding())
03666     {
03667       case XSTRINGCODING_UTF16  : switch(buffercoding)
03668                                     {
03669                                       case XSTRINGCODING_UNKWOWN  : { xbuffer.ResetPosition();
03670 
03671                                                                       for(XDWORD c=0; c<xbuffer.GetSize(); c++)
03672                                                                         {
03673                                                                           XBYTE character;
03674 
03675                                                                           xbuffer.Get(character);
03676                                                                           Add((XCHAR)character);
03677                                                                         }                                                                                                        
03678                                                                     }
03679                                                                     status = true; 
03680                                                                     break;
03681 
03682                                       case XSTRINGCODING_UTF8     : ConvertFromUTF8(xbuffer);
03683                                                                     status = true;
03684                                                                     break;
03685 
03686                                       case XSTRINGCODING_UTF16    : { xbuffer.ResetPosition();
03687 
03688                                                                       for(XDWORD c=0; c<xbuffer.GetSize(); c++)
03689                                                                         {
03690                                                                           XWORD character;
03691 
03692                                                                           xbuffer.Get(character);
03693                                                                           Add((XCHAR)character);
03694                                                                         }                                                                                                        
03695                                                                     }
03696                                                                     status = true;  
03697                                                                     break;
03698 
03699                                       case XSTRINGCODING_UTF32    : { xbuffer.ResetPosition();
03700 
03701                                                                       for(size_t c=0; c<xbuffer.GetSize(); c++) 
03702                                                                         {
03703                                                                           XDWORD value = 0;
03704 
03705                                                                           xbuffer.Get(value);
03706                                                                           if (value > 0xFFFF) 
03707                                                                             {
03708                                                                               value -= 0x10000;
03709 
03710                                                                               XDWORD lo = ((XWORD)(0xD800 | (value >> 10)));   
03711                                                                               XDWORD hi = ((XWORD)(0xDC00 | (value & 0x3FF)));
03712 
03713                                                                               //XCHAR character = ((hi<<16) | lo);  
03714                                                                               //Add((XCHAR)character);
03715 
03716                                                                               Add((XCHAR)(0xD800 | (value >> 10)));
03717                                                                               Add((XCHAR)(0xDC00 | (value & 0x3FF)));
03718                                                                             } 
03719                                                                            else 
03720                                                                             {
03721                                                                               Add((XCHAR)value);
03722                                                                             }
03723                                                                         }                                                                       
03724                                                                     } 
03725                                                                     status = true;              
03726                                                                     break;
03727 
03728                                     }
03729                                   break;
03730   
03731       case XSTRINGCODING_UTF32  : switch(buffercoding)
03732                                     {
03733                                       case XSTRINGCODING_UNKWOWN  : { xbuffer.ResetPosition();
03734 
03735                                                                       for(XDWORD c=0; c<xbuffer.GetSize(); c++)
03736                                                                         {
03737                                                                           XBYTE character;
03738 
03739                                                                           xbuffer.Get(character);
03740                                                                           Add((XCHAR)character);
03741                                                                         }                                                                                                        
03742                                                                     }
03743                                                                     status = true;  
03744                                                                     break;    
03745 
03746                                       case XSTRINGCODING_UTF8     : ConvertFromUTF8(xbuffer);
03747                                                                     status = true;  
03748                                                                     break;
03749 
03750                                       case XSTRINGCODING_UTF16    : { xbuffer.ResetPosition();
03751 
03752                                                                       for(size_t c=0; c<xbuffer.GetSize(); c++) 
03753                                                                         {
03754                                                                           XWORD value;
03755                                                                           xbuffer.Get(value);
03756 
03757                                                                           if(value >= 0xD800 && value <= 0xDBFF && c + 1 < xbuffer.GetSize()) 
03758                                                                             {
03759                                                                               XWORD low;
03760                                                                               xbuffer.Get(low);
03761 
03762                                                                               if(low >= 0xDC00 && low <= 0xDFFF) 
03763                                                                                 {
03764                                                                                   XDWORD value2 = ((value - 0xD800) << 10) + (low - 0xDC00) + 0x10000;
03765                                                                                   Add((XCHAR)value2);
03766                                                                                   c++;
03767                                                                                 } 
03768                                                                                else 
03769                                                                                 {
03770                                                                                   Add((XCHAR)value);
03771                                                                                 }
03772                                                                             } 
03773                                                                            else 
03774                                                                             {
03775                                                                               Add((XCHAR)value);
03776                                                                             }
03777                                                                         }
03778                                                                     }
03779                                                                     status = true;  
03780                                                                     break;                                                                    
03781 
03782                                       case XSTRINGCODING_UTF32    : { xbuffer.ResetPosition();
03783 
03784                                                                       for(XDWORD c=0; c<xbuffer.GetSize(); c++)
03785                                                                         {
03786                                                                           XDWORD character;
03787 
03788                                                                           xbuffer.Get(character);
03789                                                                           Add((XCHAR)character);
03790                                                                         }                                                                                                          
03791                                                                     }
03792                                                                     status = true;
03793                                                                     break;
03794 
03795                                     }
03796                                   break;
03797     }
03798 
03799   return status;
03800 }
03801 
03802 
03816 bool XSTRING::ConvertToExchangeXBuffer(XBUFFER& xbuffer, bool addzeroatend, bool inverse)
03817 {
03818   if(!ConvertToUTF16(xbuffer)) return false;
03819 
03820   if(addzeroatend)
03821     {
03822       xbuffer.Add((XWORD)0);
03823     }
03824 
03825   if(inverse)
03826     {
03827       XWORD* data = (XWORD*)xbuffer.Get();
03828       if(data)
03829         {
03830           for(XDWORD c=0; c<(xbuffer.GetSize()/2); c++)
03831             {
03832               SWAPWORD(data[c])  
03833             }
03834         }
03835     }
03836 
03837   return true;
03838 
03839 }
03840 
03841 
03851 int XSTRING::GetSizeConvertToUTF8()
03852 {
03853   XDWORD size = 0;
03854 
03855   if(IsEmpty()) return size;
03856 
03857   int i = 0;
03858   int j = 0;
03859   int c = (int)text[i++];
03860 
03861   while(c)
03862     {
03863       if(c<0)
03864         {
03865           j++;
03866         }
03867        else
03868         {
03869           if(c<128)
03870             {
03871               j++;
03872             }
03873            else
03874             {
03875                 if(c< 2048)
03876                   {
03877                     j++;
03878                     j++;
03879                   }
03880                  else
03881                   {
03882                     if(c<65536)
03883                       {
03884                         j++;
03885                         j++;
03886                         j++;
03887                       }
03888                      else
03889                       {
03890                         j++;
03891                         j++;
03892                         j++;
03893                         j++;
03894                       }
03895                   }
03896             }
03897         }
03898 
03899       c = text[i++];
03900     }
03901 
03902   size = j;
03903 
03904   return size;
03905 }
03906 
03907 
03920 bool XSTRING::ConvertFromUTF8(XBYTE* data,XDWORD size)
03921 {
03922   XDWORD  sizeutf8  = 0;
03923   XDWORD  index     = 0;
03924   XBYTE*  ptr;
03925   XDWORD  c;
03926 
03927   c   = 0;
03928   ptr = data;
03929   while(c<size)
03930     {
03931       if(((XBYTE)*ptr)<128 || (*ptr&192)==192) sizeutf8++;
03932       ptr++;
03933       c++;
03934     }
03935 
03936   if(!sizeutf8) return false;
03937 
03938   if(sizeutf8>size)  sizeutf8 = size;
03939 
03940   if(!ReAllocBuffer(sizeutf8)) return false;
03941 
03942   c   = 0;
03943   ptr = data;
03944   while(c < size)
03945     {
03946       XBYTE b=*ptr;
03947       //Byte represents an ASCII character. Direct copy will do.
03948       if(!(b&128))
03949         {
03950           text[index]=b;
03951         }
03952        else
03953         {
03954            //Byte is the middle of an encoded character. Ignore.
03955            if((b&192)==128)
03956              {
03957                ptr++;
03958                continue;
03959              }
03960             else
03961              {
03962                //Byte represents the start of an encoded character in the range
03963                //U+0080 to U+07FF
03964                if((b&224)==192)
03965                  {
03966                    text[index]=((*ptr&31)<<6)|(ptr[1]&63);
03967                  }
03968                 else
03969                  {
03970                     //Byte represents the start of an encoded character in the range
03971                     //U+07FF to U+FFFF
03972                     if((b&240)==224)
03973                       {
03974                         text[index]=((*ptr&15)<<12)|((ptr[1]&63)<<6)|(ptr[2]&63);
03975                       }
03976                      else
03977                       {
03978                         //Byte represents the start of an encoded character beyond the
03979                         //U+FFFF limit of 16-bit integers
03980                         if((b&248)==240)
03981                           {
03982                             text[index]='?';
03983                           }
03984                       }
03985                  }
03986              }
03987         }
03988 
03989       index++;
03990       ptr++;
03991       c++;
03992 
03993       if(index>=sizeutf8) break;
03994     }
03995 
03996   return true;
03997 }
03998 
03999 
04011 bool XSTRING::ConvertFromUTF8(XBUFFER& xbuffer)
04012 {
04013   return ConvertFromUTF8(xbuffer.Get(), xbuffer.GetSize());
04014 }
04015 
04016 
04030 bool XSTRING::ConvertToUTF8(XBYTE* data, int& size, bool addzeroatend)
04031 {
04032   size = 0;
04033   if(!data) return false;
04034 
04035   if(IsEmpty()) return false;
04036 
04037   int i = 0;
04038   int j = 0;
04039   int c = (int)text[i++];
04040 
04041   while(c)
04042     {
04043       if(c<0)
04044         {
04045           data[j++]=1;
04046         }
04047        else
04048         {
04049           if(c<128)
04050             {
04051               data[j++] = (XBYTE)c;
04052             }
04053            else
04054             {
04055                 if(c< 2048)
04056                   {
04057                     data[j++]= (XBYTE)(192+ c/64);
04058                     data[j++]= (XBYTE)(128+(c&63));
04059                   }
04060                  else
04061                   {
04062                     if(c<65536)
04063                       {
04064                         data[j++]=  (XBYTE)(224+ c/4096);
04065                         data[j++]=  (XBYTE)(128+(c&4095)/64);
04066                         data[j++]=  (XBYTE)(128+(c&63));
04067                       }
04068                      else
04069                       {
04070                         data[j++]=  (XBYTE)(240+ c/262144);
04071                         data[j++]=  (XBYTE)(128+(c&262143)/4096);
04072                         data[j++]=  (XBYTE)(128+(c&4095)/64);
04073                         data[j++]=  (XBYTE)(128+(c&63));
04074                       }
04075 
04076 
04077                   }
04078             }
04079         }
04080 
04081       c = text[i++];
04082     }
04083 
04084   if(addzeroatend)
04085     {
04086       data[j]=0;     
04087     }
04088    else 
04089     {
04090       j--;
04091     }
04092  
04093   size = j;
04094 
04095   return true;
04096 }
04097 
04098 
04111 bool XSTRING::ConvertToUTF8(XBUFFER& xbuffer, bool addzeroatend)
04112 {
04113   if(IsEmpty()) return false;
04114 
04115   XDWORD sizeUTF8 = GetSizeConvertToUTF8();
04116 
04117   if(addzeroatend) 
04118     {
04119       sizeUTF8++;
04120     }
04121 
04122   xbuffer.Delete();
04123   xbuffer.Resize(sizeUTF8);
04124   xbuffer.FillBuffer();
04125 
04126   int _size  = xbuffer.GetSize();
04127 
04128   return ConvertToUTF8(xbuffer.Get(),  _size, addzeroatend);
04129 }
