04143 bool XSTRING::ConvertFromUTF16(XBUFFER& xbuffer)
04144 {
04145   return ConvertFromXBuffer(xbuffer, XSTRINGCODING_UTF16);
04146 }
04147 
04148 
04160 bool XSTRING::ConvertToUTF16(XBUFFER& xbuffer)
04161 {
04162   if(IsEmpty()) return false;
04163 
04164   bool status = false;
04165 
04166   xbuffer.Empty();
04167 
04168   switch(GetTypeCoding())
04169     {
04170       case XSTRINGCODING_UTF16  : { for(XDWORD c=0; c<size; c++)
04171                                       {
04172                                         xbuffer.Add((XWORD)text[c]);
04173                                       }
04174                                   
04175                                     status = true;  
04176                                   }
04177                                   break;  
04178   
04179       case XSTRINGCODING_UTF32  : { for(size_t c=0; c<size; c++) 
04180                                       {
04181                                         XDWORD value = text[c];
04182                                         if (value > 0xFFFF) 
04183                                           {
04184                                             value -= 0x10000;
04185                                             xbuffer.Add((XWORD)(0xD800 | (value >> 10)));
04186                                             xbuffer.Add((XWORD)(0xDC00 | (value & 0x3FF)));
04187                                           } 
04188                                          else 
04189                                           {
04190                                             xbuffer.Add((XWORD)value);
04191                                           }
04192                                       }
04193 
04194                                     status = true;  
04195                                   }                               
04196                                   break;
04197     }
04198  
04199   return status;
04200 }
