04214 bool XSTRING::ConvertFromUTF32(XBUFFER& xbuffer)
04215 {
04216   return ConvertFromXBuffer(xbuffer, XSTRINGCODING_UTF32);
04217 }
04218 
04219 
04231 bool XSTRING::ConvertToUTF32(XBUFFER& xbuffer)
04232 {
04233   bool status = false;
04234 
04235   xbuffer.Empty();
04236 
04237   switch(GetTypeCoding())
04238     {
04239       case XSTRINGCODING_UTF16  : { for(size_t c=0; c<size; c++) 
04240                                       {
04241                                         XDWORD value = text[c];
04242                                         if(value >= 0xD800 && value <= 0xDBFF && c + 1 < size) 
04243                                           {
04244                                             XWORD low = text[c + 1];
04245                                             if(low >= 0xDC00 && low <= 0xDFFF) 
04246                                               {
04247                                                 value = ((value - 0xD800) << 10) + (low - 0xDC00) + 0x10000;
04248                                                 xbuffer.Add((XDWORD)value);
04249                                                 c++;
04250                                               } 
04251                                              else 
04252                                               {
04253                                                 xbuffer.Add((XDWORD)value);
04254                                               }
04255                                           } 
04256                                          else 
04257                                           {
04258                                             xbuffer.Add((XDWORD)value);
04259                                           }
04260                                       }
04261                                   }
04262                                   status = true;  
04263                                   break;
04264   
04265       case XSTRINGCODING_UTF32  : { for(XDWORD c=0; c<size; c++)
04266                                       {
04267                                         xbuffer.Add((XDWORD)text[c]);
04268                                       }
04269                                   }
04270                                   status = true;  
04271                                   break;
04272     }
04273    
04274   return status;
04275 }
