04289 bool XSTRING::ConvertToBase64(XSTRING& string)
04290 {
04291   XBUFFER data;
04292   XBUFFER charstr;
04293 
04294   string.Empty();
04295  
04296   (*this).ConvertToASCII(charstr);  
04297   data.Add((XBYTE*)charstr.Get(), GetSize());  
04298   
04299   string.ConvertBinaryToBase64(data);
04300 
04301   return string.GetSize()?true:false;
04302 }
