04410 bool XSTRING::ConvertFromDoubleToSpanishText(double value, bool withintegerpart, bool withdecimalpart, double& integerpart, double& decimalpart)
04411 {
04412   Empty();
04413 
04414   integerpart = Truncate(value);
04415   decimalpart = Fraction(value);
04416 
04417   XSTRING tmp;
04418 
04419   if(withintegerpart)
04420     {
04421       double _integerpart;
04422       double _decimalpart;
04423 
04424       if(integerpart == 0) Set(__L("cero"));
04425         else if(integerpart ==  1) Set(__L("uno"));
04426         else if(integerpart ==  2) Set(__L("dos"));
04427         else if(integerpart ==  3) Set(__L("tres"));
04428         else if(integerpart ==  4) Set(__L("cuatro"));
04429         else if(integerpart ==  5) Set(__L("cinco"));
04430         else if(integerpart ==  6) Set(__L("seis"));
04431         else if(integerpart ==  7) Set(__L("siete"));
04432         else if(integerpart ==  8) Set(__L("ocho"));
04433         else if(integerpart ==  9) Set(__L("nueve"));
04434         else if(integerpart == 10) Set(__L("diez"));
04435         else if(integerpart == 11) Set(__L("once"));
04436         else if(integerpart == 12) Set(__L("doce"));
04437         else if(integerpart == 13) Set(__L("trece"));
04438         else if(integerpart == 14) Set(__L("catorce"));
04439         else if(integerpart == 15) Set(__L("quince"));
04440         else if(integerpart < 20)
04441                {
04442                   Set(__L("dieci"));
04443                   tmp.ConvertFromDoubleToSpanishText(integerpart - 10, true, false, _integerpart, _decimalpart);
04444                   Add(tmp);
04445                 }
04446                else if(integerpart == 20) Set(__L("veinte"));
04447                       else if(integerpart < 30)
04448                             {
04449                               Set(__L("veinti"));
04450                               tmp.ConvertFromDoubleToSpanishText(integerpart - 20, true, false, _integerpart, _decimalpart);
04451                               Add(tmp);
04452                              }
04453                             else if(integerpart == 30) Set(__L("treinta"));
04454                             else if(integerpart == 40) Set(__L("cuarenta"));
04455                             else if(integerpart == 50) Set(__L("cincuenta"));
04456                             else if(integerpart == 60) Set(__L("sesenta"));
04457                             else if(integerpart == 70) Set(__L("setenta"));
04458                             else if(integerpart == 80) Set(__L("ochenta"));
04459                             else if(integerpart == 90) Set(__L("noventa"));
04460                             else if(integerpart < 100)
04461                                    {
04462                                      ConvertFromDoubleToSpanishText(Truncate(integerpart/10) * 10, true, false, _integerpart, _decimalpart);
04463                                      Add(__L(" y "));
04464                                      tmp.ConvertFromDoubleToSpanishText(fmod(integerpart, (double)10), true, false, _integerpart, _decimalpart);
04465                                      Add(tmp);
04466                                    }
04467                                   else if(integerpart == 100) Set(__L("cien"));
04468                                   else if(integerpart < 200)
04469                                          {
04470                                            Set(__L("ciento "));
04471                                            tmp.ConvertFromDoubleToSpanishText(integerpart - 100, true, false, _integerpart, _decimalpart);
04472                                            Add(tmp);
04473                                          }
04474                                         else if((integerpart == 200) || (integerpart == 300) || (integerpart == 400) || (integerpart == 600) || (integerpart == 800))
04475                                                {
04476                                                  ConvertFromDoubleToSpanishText(Truncate(integerpart / 100), true, false, _integerpart, _decimalpart);
04477                                                  Add(__L("cientos"));
04478                                                }
04479                                               else if(integerpart == 500) Set(__L("quinientos"));
04480                                               else if(integerpart == 700) Set(__L("setecientos"));
04481                                               else if(integerpart == 900) Set(__L("novecientos"));
04482                                               else if(integerpart < 1000)
04483                                                    {
04484                                                      ConvertFromDoubleToSpanishText(Truncate(integerpart / 100) * 100, true, false, _integerpart, _decimalpart);
04485                                                      Add(__L(" "));
04486                                                      tmp.ConvertFromDoubleToSpanishText(fmod(integerpart, (double)100), true, false, _integerpart, _decimalpart);
04487                                                      Add(tmp);
04488                                                    }
04489                                                   else if(integerpart == 1000) Set(__L("mil"));
04490                                                   else if(integerpart < 2000)
04491                                                         {
04492                                                           Set(__L("mil "));
04493                                                           tmp.ConvertFromDoubleToSpanishText(fmod(integerpart, (double)1000), true, false, _integerpart, _decimalpart);
04494                                                           Add(tmp);
04495                                                         }
04496                                                       else if(integerpart < 1000000)
04497                                                               {
04498                                                                 ConvertFromDoubleToSpanishText(Truncate(integerpart / 1000), true, false, _integerpart, _decimalpart);
04499                                                                 Add(__L(" mil"));
04500                                                                 if((fmod(integerpart, (double)1000)) > 0)
04501                                                                   {
04502                                                                     Add(" ");
04503                                                                     tmp.ConvertFromDoubleToSpanishText(fmod(integerpart, (double)1000), true, false, _integerpart, _decimalpart);
04504                                                                     Add(tmp);
04505                                                                   }
04506                                                               }
04507                                                             else if(integerpart == 1000000) Set(__L("un millon"));
04508                                                             else if(integerpart < 2000000)
04509                                                                     {
04510                                                                       Set(__L("un millon"));
04511                                                                       tmp.ConvertFromDoubleToSpanishText(fmod(integerpart, (double)1000000), true, false, _integerpart, _decimalpart);
04512                                                                       Add(tmp);
04513                                                                     }
04514                                                                    else if(integerpart < 1000000000000LL)
04515                                                                           {
04516                                                                              ConvertFromDoubleToSpanishText((integerpart / 1000000), true, false, _integerpart, _decimalpart);
04517                                                                              Add(__L(" millones"));
04518                                                                              if((integerpart - Truncate(integerpart / 1000000) * 1000000) > 0)
04519                                                                                {
04520                                                                                  Add(__L(" "));
04521                                                                                  tmp.ConvertFromDoubleToSpanishText(integerpart - Truncate(integerpart / 1000000) * 1000000, true, false, _integerpart, _decimalpart);
04522                                                                                  Add(tmp);
04523                                                                                }
04524                                                                           }
04525                                                                          else
04526                                                                           {
04527                                                                             if(integerpart == 1000000000000LL) Set(__L("un billon"));
04528                                                                               else if(integerpart < 2000000000000LL)
04529                                                                                 {
04530                                                                                   Set(__L("un billon"));
04531                                                                                   tmp.ConvertFromDoubleToSpanishText(Truncate(integerpart / 1000000000000LL) * 1000000000000LL, true, false, _integerpart, _decimalpart);
04532                                                                                   Add(tmp);
04533                                                                                 }
04534                                                                                else
04535                                                                                 {
04536                                                                                    ConvertFromDoubleToSpanishText(Truncate(integerpart / 1000000000000LL), true, false, _integerpart, _decimalpart);
04537                                                                                    Add(__L(" billones"));
04538 
04539                                                                                    if((integerpart - Truncate(integerpart / 1000000000000LL) * 1000000000000LL) > 0)
04540                                                                                      {
04541                                                                                        Add(__L(" "));
04542                                                                                        tmp.ConvertFromDoubleToSpanishText(integerpart - Truncate(integerpart / 1000000000000LL) * 1000000000000LL, true, false, _integerpart, _decimalpart);
04543                                                                                        Add(tmp);
04544                                                                                      }
04545                                                                                 }
04546                                                                           }
04547       if(!integerpart) return false;
04548     }
04549 
04550   if(withdecimalpart)
04551     {
04552       double _integerpart;
04553       double _decimalpart;
04554 
04555       double ipart = Truncate(decimalpart*100);
04556       double dpart = Fraction(decimalpart*100);
04557 
04558       if(dpart>=0.9f) ipart++;
04559 
04560       if(ipart)
04561         {
04562           if(withintegerpart) Add(__L(" con "));
04563           tmp.ConvertFromDoubleToSpanishText(ipart, true, false, _integerpart, _decimalpart);
04564           Add(tmp);
04565 
04566         } else return false;
04567     }
04568 
04569   return true;
04570 }
