04582 bool XSTRING::Swab()
04583 {
04584   if(IsEmpty()) return false;
04585 
04586   for(int c=0;c<(int)GetSize();c+=2)
04587     {
04588       XCHAR data;
04589 
04590       data      = text[c+1];
04591       text[c+1] = text[c];
04592       text[c]   = data;
04593     }
04594 
04595   return true;
04596 }
