04598 
04612 bool XSTRING::Split(XCHAR separator, XVECTOR<XSTRING*>& results, bool addsubstringempty)
04613 {
04614   if(IsEmpty()) return false;
04615 
04616   int start = 0;
04617 
04618   for(XDWORD c=0; c<sizemem; c++)
04619     {
04620       if((text[c] == separator) || (!text[c]))
04621         {
04622           XSTRING* string = new XSTRING();
04623           if(string)
04624             {
04625               Copy(start, (*string));
04626 
04627               for(XDWORD d=0; d<string->GetSize(); d++)
04628                 {
04629                   if((string->Get()[d] == separator) || (!string->Get()[d]))
04630                     {
04631                       string->Get()[d] = 0;
04632                       break;
04633                     }
04634                 }
04635 
04636               string->AdjustSize();
04637 
04638               if(addsubstringempty)
04639                 {
04640                   if(c)
04641                         results.Add(string);
04642                    else delete string;
04643                 }
04644                else
04645                 {
04646                   if(string->GetSize())
04647                         results.Add(string);
04648                    else delete string;
04649                 }
04650 
04651               start = c+1;
04652 
04653               if((XDWORD)start >= size) break;
04654             }
04655         }
04656     }
04657 
04658   return true;
04659 }
