04661 
04675 bool XSTRING::FormatArg(const XCHAR* mask, va_list* arg, bool isspecialweb)
04676 {
04677   XSTRING str;
04678   bool    openlabel = false;
04679 
04680   Empty();
04681 
04682   if(!mask) return false;
04683 
04684   int c  = 0;
04685 
04686   XCHAR* param = new XCHAR[16];
04687   if(param)
04688     {
04689       while(mask[c])
04690         {
04691           switch(mask[c])
04692             {
04693               case __C('%')   : { int  nparam = 1;
04694                                   bool end    = false;
04695 
04696                                   memset(param,0, 16*sizeof(XCHAR));
04697                                   param[0] = __C('%');
04698 
04699                                   c++;
04700 
04701                                   do{ param[nparam] = mask[c];
04702 
04703                                       nparam++;
04704 
04705                                       switch(mask[c])
04706                                         {
04707                                           case __C('c')   :
04708                                           case __C('C')   : { XCHAR character;
04709 
04710                                                               #if defined(LINUX) || defined(ANDROID) || defined(MICROCONTROLLER)
04711                                                               character = (XCHAR)va_arg((*arg), XDWORD);
04712                                                               #else
04713                                                               character = (XCHAR)va_arg((*arg), int /*XCHAR*/);
04714                                                               #endif
04715 
04716                                                               str.Set(character);
04717                                                               end  = true ;
04718                                                             }
04719                                                             break;
04720    
04721                                           case __C('i')   :
04722                                           case __C('o')   :                                         
04723                                           case __C('x')   :
04724                                           case __C('X')   : { bool large = false;
04725                                                               for(int l=0; l<(int)XSTRING::GetSize(param); l++)
04726                                                                 {
04727                                                                   if(param[l] == __C('l')) large = true;
04728                                                                 }
04729 
04730                                                               if(!large)
04731                                                                 {
04732                                                                   int value = (int)va_arg((*arg), int);
04733 
04734                                                                   str.ConvertFromInt(value, param);
04735                                                                 }
04736                                                                else
04737                                                                 {
04738                                                                   str.ConvertFromLongLong((long long)va_arg((*arg), long long), param);
04739                                                                 }
04740                                                               end  = true;
04741                                                             }                                                           
04742                                                             break;
04743 
04744                                           case __C('d')   :                                       
04745                                           case __C('u')   : { bool large    = false;
04746                                                               bool shortint = false;
04747     
04748                                                               for(int d=0; d<(int)XSTRING::GetSize(param); d++)
04749                                                                 {
04750                                                                   if(param[d] == __C('l')) 
04751                                                                     {
04752                                                                       large = true;
04753                                                                     }
04754 
04755                                                                   if(param[d] == __C('h')) 
04756                                                                     {
04757                                                                       shortint = true;
04758                                                                     }
04759                                                                 }
04760 
04761                                                               if(shortint)
04762                                                                 {
04763                                                                   if(mask[c] == __C('d')) 
04764                                                                     {
04765                                                                       short value = (short)va_arg((*arg), int);
04766                                                                       str.ConvertFromShort(value, param);
04767                                                                       end  = true;
04768                                                                     }
04769 
04770                                                                   if(mask[c] == __C('u')) 
04771                                                                     {
04772                                                                       XWORD value = (XWORD)va_arg((*arg), int);
04773                                                                       str.ConvertFromWord(value, param);
04774                                                                       end  = true;
04775                                                                     }
04776                                                                 }  
04777 
04778                                                               if((!shortint) && (!large))
04779                                                                 {
04780                                                                   if(mask[c] == __C('d')) 
04781                                                                     {
04782                                                                       int value = (int)va_arg((*arg), int);
04783                                                                       str.ConvertFromInt(value, param);
04784                                                                       end  = true;
04785                                                                     }
04786 
04787                                                                   if(mask[c] == __C('u')) 
04788                                                                     {
04789                                                                       XDWORD value = (XDWORD)va_arg((*arg), int);
04790                                                                       str.ConvertFromDWord(value, param);
04791                                                                       end  = true;
04792                                                                     }
04793                                                                 }  
04794 
04795                                                               if(large)
04796                                                                 {
04797                                                                   if(mask[c] == __C('d')) 
04798                                                                     {
04799                                                                       long long value = (long long)va_arg((*arg), long long);
04800                                                                       str.ConvertFromLongLong(value, param);
04801                                                                       end  = true;
04802                                                                     }
04803                                                               
04804                                                                   if(mask[c] == __C('u')) 
04805                                                                     {
04806                                                                       XQWORD value = va_arg((*arg), long long);
04807                                                                       str.ConvertFromLongLong(value, param);  
04808                                                                       end  = true;
04809                                                                     }
04810                                                                 }                                                                                                                                                                                       
04811                                                             }
04812                                                             break;
04813 
04814                                           case __C('f')   : { bool large = false;
04815                                                               for(int d=0; d<(int)XSTRING::GetSize(param); d++)
04816                                                                 {
04817                                                                   if(param[d] == __C('l')) large = true;
04818                                                                 }
04819 
04820                                                               if(!large)
04821                                                                 {  
04822                                                                   double data = va_arg((*arg), double);
04823                                                                   str.ConvertFromFloat((float)(data), param);
04824                                                                   end  = true;
04825                                                                 }
04826                                                                else                                                                
04827                                                                 { 
04828                                                                   double data = va_arg((*arg), double);
04829                                                                   str.ConvertFromDouble((double)data, param);
04830                                                                   end  = true;
04831                                                                 }                                                                   
04832                                                             }
04833                                                             break;
04834 
04835                                           case __C('g')   :
04836                                           case __C('G')   : { double data = va_arg((*arg), double);
04837 
04838                                                               str.ConvertFromDouble(data, param);
04839                                                               end  = true;
04840                                                             }
04841                                                             break;
04842 
04843                                           case __C('e')   :
04844                                           case __C('E')   :
04845 
04846                                           case __C('n')   :
04847                                           case __C('p')   : end = true;
04848                                                             break;
04849 
04850                                           case __C('s')   :
04851                                           case __C('S')   : { str = (XCHAR*)va_arg((*arg),XCHAR*);
04852 
04853                                                               XSTRING sizestr;
04854 
04855                                                               sizestr = param;
04856                                                               if(sizestr.GetSize()>2)
04857                                                                 {
04858                                                                   sizestr.ToUpperCase();
04859                                                                   sizestr.DeleteCharacter(__C('%'));
04860                                                                   sizestr.DeleteCharacter(__C('S'));
04861 
04862                                                                   if(!sizestr.IsEmpty())
04863                                                                     {
04864                                                                       int sizeadjust = sizestr.ConvertToInt();
04865                                                                       if(abs((int)sizeadjust) > (int)str.GetSize())
04866                                                                         {
04867                                                                           str.AdjustSize(abs((int)sizeadjust),(sizeadjust>0)?true:false, isspecialweb?__L("&nbsp"):__L(" "), isspecialweb);
04868                                                                         }
04869                                                                     }
04870                                                                 }
04871 
04872                                                               end = true;
04873                                                             }
04874                                                             break;
04875 
04876                                           case __C('%')   : str = __L("%");
04877                                                             end = true;
04878                                                             break;
04879 
04880                                           case __C('\x0') : end = true;
04881                                                             break;
04882 
04883                                               default     : break;
04884                                         }
04885 
04886                                       c++;
04887 
04888                                     } while(!end);
04889                                 }
04890                                 break;
04891 
04892               case __C('<')   : { openlabel = true;
04893                                   XCHAR character[2]={ mask[c], 0 };
04894                                   str.Set(character);
04895                                   c++;
04896                                 }
04897                                 break;
04898 
04899               case __C('>')   : { openlabel = false;
04900                                   XCHAR character[2]={ mask[c], 0 };
04901                                   str.Set(character);
04902                                   c++;
04903                                 }
04904                                 break;
04905 
04906               case 0x0A       : if(isspecialweb && (!openlabel))
04907                                   {
04908                                     str = __L("<br />");
04909                                   }
04910                                  else
04911                                   {
04912                                     XCHAR character[2]={ mask[c], 0 };
04913                                     str.Set(character);
04914                                   }
04915                                 c++;
04916                                 break;
04917 
04918               case 0x20       : if(isspecialweb && (!openlabel))
04919                                   {
04920                                     str = __L("&nbsp");
04921                                   }
04922                                  else
04923                                   {
04924                                     XCHAR character[2]={ mask[c], 0 };
04925                                     str.Set(character);
04926                                   }
04927                                 c++;
04928                                 break;
04929 
04930                  default      : { XCHAR   character[2]={ mask[c], 0 };
04931                                   XDWORD  value       = (XDWORD)mask[c];
04932 
04933                                   if(isspecialweb && (!openlabel) && (value > 128))
04934                                         str.Format(__L("&#%d"), value);
04935                                    else str.Set(character);
04936 
04937                                   c++;
04938                                 }
04939                                 break;
04940             }
04941 
04942           Add(str);
04943         }
04944 
04945       delete [] param;
04946     }
04947 
04948   return true;
04949 }
