05006 
05019 bool XSTRING::UnFormat(const XCHAR* mask,...)
05020 {
05021   if(!size)    return false;
05022   if(!mask)    return false;
05023   if(!mask[0]) return false;
05024   if(!text)    return false;
05025   if(!text[0]) return false;
05026 
05027   va_list arg;
05028   va_start(arg, mask);
05029 
05030   XCHAR* param = new XCHAR[16];
05031   if(param)
05032     {
05033       int   nparam;
05034       bool  end;
05035       int   indextext = 0;
05036       int   c         = 0;
05037       bool  inbrackets  = false;
05038 
05039       while(mask[c])
05040         {
05041           switch(mask[c])
05042             {
05043               case __C('%')   : memset(param,0,16*sizeof(XCHAR));
05044                                 param[0] = __C('%');
05045 
05046                                 nparam = 1;
05047                                 end    = false;
05048 
05049                                 c++;
05050 
05051                                 do{ param[nparam] = mask[c];
05052 
05053                                     nparam++;
05054 
05055                                     switch(mask[c])
05056                                       {
05057                                         case __C('c')   :
05058                                         case __C('C')   : { value_xchar = (XCHAR*)va_arg(arg, XCHAR*);
05059                                                             if(value_xchar) (*value_xchar) = Get()[indextext];
05060                                                             indextext++;
05061                                                             end  = true;
05062                                                           }
05063                                                           break;
05064 
05065                                         case __C('i')   :
05066                                         case __C('o')   :
05067                                         case __C('x')   :
05068                                         case __C('X')   : { value_int = (int*)va_arg(arg, int*);
05069 
05070                                                             if(value_int) (*value_int) = ConvertToInt(indextext,(XCHAR*)param, false);
05071                                                             end  = true;
05072                                                           }              
05073                                                           break;
05074 
05075                                         case __C('d')   :                                       
05076                                         case __C('u')   : { bool large    = false;
05077                                                             bool shortint = false;
05078     
05079                                                             for(int d=0; d<(int)XSTRING::GetSize(param); d++)
05080                                                               {
05081                                                                 if(param[d] == __C('l')) 
05082                                                                   {
05083                                                                     large = true;
05084                                                                   }
05085 
05086                                                                 if(param[d] == __C('h')) 
05087                                                                   {
05088                                                                     shortint = true;
05089                                                                   }
05090                                                               }
05091 
05092                                                             if(shortint)
05093                                                               {
05094                                                                 if(mask[c] == __C('d')) 
05095                                                                   {
05096                                                                     value_short = (short*)va_arg(arg, int*);
05097 
05098                                                                     if(value_short) (*value_short) = ConvertToInt(indextext,(XCHAR*)param, false);
05099                                                                     end  = true;
05100                                                                   }
05101 
05102                                                                 if(mask[c] == __C('u')) 
05103                                                                   {
05104                                                                     value_word = (XWORD*)va_arg(arg, int*);
05105 
05106                                                                     if(value_word) (*value_word) = ConvertToDWord(indextext,(XCHAR*)param, false);
05107                                                                     end  = true;
05108                                                                   }
05109                                                               }  
05110 
05111                                                             if((!shortint) && (!large))
05112                                                               {
05113                                                                 if(mask[c] == __C('d')) 
05114                                                                   {
05115                                                                     value_int = (int*)va_arg(arg, int*);
05116 
05117                                                                     if(value_int) (*value_int) = ConvertToInt(indextext,(XCHAR*)param, false);
05118                                                                     end  = true;
05119                                                                   }
05120 
05121                                                                 if(mask[c] == __C('u')) 
05122                                                                   {
05123                                                                     value_dword = (XDWORD*)va_arg(arg, int*);
05124 
05125                                                                     if(value_dword) (*value_dword) = ConvertToDWord(indextext,(XCHAR*)param, false);
05126                                                                     end  = true;
05127                                                                   }
05128                                                               }  
05129 
05130                                                             if(large)
05131                                                               {
05132                                                                 if(mask[c] == __C('d')) 
05133                                                                   {
05134                                                                     value_longlong = (long long*)va_arg(arg, long long*);
05135 
05136                                                                     if(value_longlong) (*value_longlong) = ConvertToInt(indextext,(XCHAR*)param, false);
05137                                                                     end  = true;
05138                                                                   }
05139 
05140                                                                 if(mask[c] == __C('u')) 
05141                                                                   {
05142                                                                     value_qword = (XQWORD*)va_arg(arg, long long*);
05143 
05144                                                                     if(value_qword) (*value_qword) = ConvertToDWord(indextext,(XCHAR*)param, false);
05145                                                                     end  = true;
05146                                                                   }
05147                                                               }                                                                                                                                                                                    
05148                                                           }
05149                                                           break;
05150 
05151                                         case __C('f')   : { bool large = false;
05152                                                             for(int d=0; d<(int)XSTRING::GetSize(param); d++)
05153                                                               {
05154                                                                 if(param[d] == __C('l')) large = true;
05155                                                               }
05156 
05157                                                             if(!large)
05158                                                               {  
05159                                                                 value_float = (float*)va_arg(arg, double*);
05160                                                                 if(value_float) (*value_float) = ConvertToFloat(indextext,(XCHAR*)param, false);
05161                                                                 end  = true;
05162                                                               }
05163                                                               else                                                                
05164                                                               { 
05165                                                                 value_double = (double*)va_arg(arg, double*);
05166                                                                 if(value_double) (*value_double) = ConvertToDouble(indextext,(XCHAR*)param, false);
05167                                                                 end  = true;
05168                                                               }                                                                   
05169                                                           }
05170                                                           break;
05171 
05172                                         case __C('g')   :
05173                                         case __C('G')   : { value_double = (double*)va_arg(arg, double*);
05174 
05175                                                             if(value_double) (*value_double) = ConvertToDouble(indextext,(XCHAR*)param, false);
05176 
05177                                                             end  = true;
05178                                                           }
05179                                                           break;
05180 
05181                                         case __C('e')   :
05182                                         case __C('E')   :
05183 
05184                                         case __C('n')   :
05185                                         case __C('p')   : end = true;
05186                                                           break;
05187 
05188                                         case __C('s')   :
05189                                         case __C('S')   : { value_xchar = (XCHAR*)va_arg(arg,XCHAR*);
05190                                                             if(!value_xchar) break;
05191 
05192                                                             XSTRING string;
05193 
05194                                                             int d = 0;
05195                                                             while(text[d+indextext]!=mask[c+1] /*&& text[d+indextext]!=__C(' ')*/)
05196                                                               {
05197                                                                 if(!text[indextext+d]) break;
05198                                                                 d++;
05199                                                               }
05200 
05201                                                             Copy(indextext, indextext+d, string);
05202 
05203                                                             if(nparam>2)
05204                                                               {
05205                                                                 XSTRING strsizeparam = param;
05206                                                                 int     sizeparam;
05207 
05208                                                                 strsizeparam.DeleteCharacters(0,1);
05209                                                                 strsizeparam.DeleteCharacters(strsizeparam.GetSize()-1,1);
05210 
05211                                                                 sizeparam = abs((int)strsizeparam.ConvertToInt());
05212                                                                 if(sizeparam)
05213                                                                   {
05214                                                                     if(string.GetSize()>(XDWORD)sizeparam)  string.DeleteCharacters(sizeparam, (string.GetSize()-sizeparam));
05215                                                                   }
05216                                                               }
05217 
05218                                                             memcpy((XBYTE*)value_xchar, (XBYTE*)string.Get(), (string.GetSize()*sizeof(XCHAR)));
05219                                                             value_xchar[string.GetSize()] = 0;
05220 
05221                                                             indextext += string.GetSize();
05222 
05223                                                             end = true;
05224                                                           }
05225                                                           break;
05226 
05227                                         case __C('%')   : end = true;
05228                                                           break;
05229 
05230                                         case __C('[')   : inbrackets  = true;
05231                                                           break;
05232 
05233                                         case __C(']')   : if(inbrackets)
05234                                                             {
05235                                                               value_xchar = (XCHAR*)va_arg(arg, XCHAR*);
05236 
05237                                                               ConvertStringWithMask(param, &text[indextext], value_xchar);
05238                                                               indextext += GetSize(value_xchar);
05239 
05240                                                               if((mask[c+1] == __C('s')) || (mask[c+1] == __C('S'))) c++;
05241                                                               end = true;
05242                                                             }
05243                                                           break;
05244 
05245                                         case __C('\x0') : end = true;
05246                                                           break;
05247 
05248                                             default     : break;
05249                                       }
05250 
05251                                     c++;
05252 
05253                                     if(end)
05254                                       {
05255                                         if(mask[c]!=__C('%'))
05256                                           {
05257                                             while(text[indextext] != mask[c])
05258                                               {
05259                                                 if(!text[indextext]) break;
05260                                                 indextext++;
05261                                               }
05262                                           }
05263                                          else
05264                                           {
05265                                             //if(text[indextext] ==__C(' ')) indextext++;
05266                                           }
05267                                       }
05268 
05269                                   } while(!end);
05270 
05271                               break;
05272 
05273                     default : c++;
05274                               indextext++;
05275                               break;
05276             }
05277         }
05278 
05279      delete [] param;
05280    }
05281 
05282   va_end(arg);
05283 
05284   return true;
05285 }
05286 
05287 
05299 int XSTRING::FindCharacterFromSet(const XCHAR* chars)
05300 {
05301   XCHAR   c;
05302   XCHAR*  s;
05303   XCHAR*  p;
05304 
05305   for(s = text, c = *s; c != 0; s++, c = *s)
05306     {
05307       for(p = (XCHAR*)chars; *p != 0; p++)
05308         {
05309           if(c == *p)
05310             {
05311               return (int)(s - text);
05312             }
05313         }
05314     }
05315 
05316   return (int)(s - text);
05317 }
05318 
05319 
05334 bool XSTRING::AdjustSize(XDWORD size, bool ahead, XCHAR* characters, bool addstring)
05335 {
05336   if(this->size == size) return false;
05337 
05338   int diference;
05339 
05340   if(this->size > size)
05341     {
05342        diference = (this->size- size);
05343        DeleteCharacters(size,diference);
05344     }
05345    else
05346     {
05347       if(characters)
05348         {
05349           if(ahead)
05350             {
05351               XSTRING string;
05352 
05353               diference = (size-this->size);
05354               for(int c=0;c<diference;c++)
05355                 {
05356                   if(addstring) string.Add(characters); else string.Add(characters[0]);
05357                 }
05358 
05359               string+=Get();
05360 
05361               Set(string);
05362 
05363             }
05364            else
05365             {
05366               diference = ((size)-this->size);
05367               for(int c=0;c<diference;c++)
05368                 {
05369                   if(addstring) Add(characters);  else Add(characters[0]);
05370                 }
05371             }
05372         }
05373        else
05374         {
05375           diference = ((size)-this->size);
05376 
05377           ReAllocBuffer(size);
05378           if(ahead)
05379             {
05380               for(int c=0;c<diference;c++)
05381                 {
05382                   text[c+diference] = text[c];
05383                   text[c] = __C('-');
05384                 }
05385             }
05386         }
05387     }
05388 
05389   return true;
05390 }
05391 
05392 
05402 bool XSTRING::AdjustSize()
05403 {
05404   int c;
05405 
05406   for(c=0;c<(int)size;c++)
05407     {
05408       if(!text[c]) break;
05409     }
05410 
05411   if(!c)
05412     {
05413       return FreeBuffer();
05414     }
05415 
05416   return AdjustSize(c, false, __L(" "), false);
05417 }
