05419 
05431 bool XSTRING::BinaryCompare(XSTRING& string)
05432 {
05433   if(size != string.size)
05434     {
05435       return false;
05436     }
05437 
05438   return memcmp(text, string.text, size*sizeof(XCHAR)) == 0;
05439 }
