05441 
05454 bool XSTRING::Explode(XCHAR token, XVECTOR<XSTRING*>* receive)
05455 {
05456   int start = 0;
05457 
05458   do{ int end = FindCharacter(token,start);
05459       if(end==XSTRING_NOTFOUND) end = size;
05460 
05461       if((end-start)>0)
05462         {
05463           XSTRING* tok=new XSTRING();
05464           if(!tok)  return false;
05465 
05466           Copy(start, end, (*tok));
05467 
05468           if(!receive->Add(tok))
05469             {
05470               delete(tok);
05471               return false;
05472             }
05473         }
05474 
05475       start=end+1;
05476 
05477     } while(start < (int)size);
05478 
05479   return true;
05480 }
05481 
05482 
05494 bool XSTRING::GetTypeOfLineEnd(XSTRING& lineend)
05495 {
05496   //----------------
05497   // \n      x        0
05498   // \r      x        1
05499   // \n      \r       2
05500   // \r      \n       3
05501 
05502   int combinations[4] = { 0, 0, 0, 0 };
05503 
05504   if(GetSize()<2) return false;
05505 
05506   for(XDWORD c=0; c<GetSize()-1; c++)
05507     {
05508       if(Get()[c] == __C('\n'))
05509         {
05510           if(Get()[c+1] == __C('\r'))
05511             {
05512               combinations[2]++;
05513               c+=2;
05514 
05515             } else combinations[0]++;
05516         }
05517        else
05518         {
05519           if(Get()[c] == __C('\r'))
05520             {
05521               if(Get()[c+1] == __C('\n'))
05522                 {
05523                   combinations[3]++;
05524                   c+=2;
05525                 }
05526                else combinations[1]++;
05527             }
05528         }
05529     }
05530 
05531   if((combinations[0] >= combinations[1]) && (combinations[0] >= combinations[2]) && (combinations[0] >= combinations[3]))
05532     {
05533       lineend = __L("\n");
05534     }
05535    else
05536     {
05537       if((combinations[1] >= combinations[0]) && (combinations[1] >= combinations[2]) && (combinations[1] >= combinations[3]))
05538         {
05539           lineend = __L("\r");
05540         }
05541        else
05542         {
05543           if((combinations[2] >= combinations[0]) && (combinations[2] >= combinations[1]) && (combinations[2] >= combinations[3]))
05544             {
05545               lineend = __L("\n\r");
05546             }
05547            else
05548             {
05549               if((combinations[3] >= combinations[0]) && (combinations[3] >= combinations[1]) && (combinations[3] >= combinations[2]))
05550                 {
05551                   lineend = __L("\r\n");
05552                 }
05553             }
05554         }
05555     }
05556 
05557   return lineend.GetSize()?true:false;
05558 }
05559 
05560 
05572 bool XSTRING::ReAllocBuffer(XDWORD sizechar)
05573 {
05574   if(!sizechar)
05575     {
05576       FreeBuffer();
05577 
05578       return true;
05579     }
05580 
05581   XDWORD newsizechar      = (sizechar + XSTRING_EXCESSCHARS);
05582   XDWORD newsizecharbytes = (newsizechar * sizeof(XCHAR));
05583   XCHAR* newtext          = (XCHAR*)new XBYTE[newsizecharbytes];
05584   if(!newtext) 
05585     {
05586       return false;
05587     }
05588 
05589   memset(newtext, 0, newsizecharbytes);
05590 
05591   if(text)
05592     {
05593       if(this->size >= sizechar)
05594         { 
05595           memcpy(newtext, text, sizechar * sizeof(XCHAR));
05596         }
05597        else
05598         {
05599           memcpy(newtext, text, size * sizeof(XCHAR));
05600         } 
05601 
05602       FreeBuffer();
05603     }
05604 
05605   text          = newtext;
05606   this->size    = sizechar;
05607   this->sizemem = newsizechar;
05608 
05609   return true;
05610 }
05611 
05612 
05622 bool XSTRING::FreeBuffer()
05623 {
05624   if(xmutexfreemen) 
05625     {
05626       xmutexfreemen->Lock();
05627     }
05628 
05629   bool status = false;
05630 
05631   if(text) 
05632     {
05633       delete [] text;
05634 
05635       text    = NULL;
05636       size    = 0;
05637       sizemem = 0;
05638 
05639       status = true;
05640     }
05641 
05642   if(xmutexfreemen) 
05643     {
05644       xmutexfreemen->UnLock();
05645     }
05646 
05647   return status;
05648 }
