05650 
05664 bool XSTRING::ConvertStringWithMask(XCHAR* mask, XCHAR* string, XCHAR* result)
05665 {
05666   XSTRING  _mask;
05667   XSTRING  _string;
05668   XSTRING  _result;
05669 
05670   _mask   = mask;
05671   _string = string;
05672 
05673   char* resultchar = new char[XSTRING_MAXTEMPOSTR];
05674   if(!resultchar) return false;
05675 
05676   memset(resultchar, 0, XSTRING_MAXTEMPOSTR);
05677   
05678   XBUFFER maskchar;
05679   XBUFFER stringchar;
05680  
05681   _mask.ConvertToASCII(maskchar);
05682   _string.ConvertToASCII(stringchar);
05683  
05684   SSCANF(stringchar.GetPtrChar(), maskchar.GetPtrChar(), resultchar, XSTRING_MAXTEMPOSTR);
05685 
05686   _result = resultchar;
05687 
05688   memcpy(result, _result.Get(), ((_result.GetSize()+1)*sizeof(XCHAR)));
05689 
05690   return true;
05691 }
05692 
05693 
05705 void XSTRING::Base64_EncodeBlock(XBYTE *in, XBYTE *out, int len)
05706 {
05707   out[0] = (XBYTE) codetablebase64[ (int)(in[0] >> 2) ];  
05708   out[1] = (XBYTE) codetablebase64[ (int)(((in[0] & 0x03) << 4) | ((in[1] & 0xf0) >> 4)) ];
05709   out[2] = (XBYTE) (len > 1 ? codetablebase64[ (int)(((in[1] & 0x0f) << 2) | ((in[2] & 0xc0) >> 6)) ] : '=');
05710   out[3] = (XBYTE) (len > 2 ? codetablebase64[ (int)(in[2] & 0x3f) ] : '=');
05711 }
05712 
05713 
05727 bool XSTRING::Base64_Encode(XBYTE* buffer, XDWORD buffersize, XSTRING& line)
05728 {
05729   XBYTE in[3]     = { 0 };
05730     XBYTE out[4]    = { 0 };
05731   int   i;
05732   int   len;
05733   int   cin       = 0;
05734   
05735     *in   = (XBYTE) 0;
05736     *out  = (XBYTE) 0;
05737 
05738   while((XDWORD)cin < buffersize) 
05739     {
05740       len = 0;
05741       for(i = 0; i < 3; i++) 
05742         {
05743           in[i] = (XBYTE)buffer[cin++];
05744 
05745           if((XDWORD)cin <= buffersize) 
05746             {
05747               len++;
05748             }
05749            else 
05750             {
05751               in[i] = (XBYTE)0;
05752             }
05753         }
05754 
05755       if(len > 0) 
05756         {
05757           Base64_EncodeBlock(in, out, len );
05758           line.Add(out, 4);                 
05759         }      
05760     }
05761 
05762   return true;
05763 }
05764 
05765 
05776 void XSTRING::Base64_DecodeBlock(XBYTE in[4], XBYTE out[3]) 
05777 {
05778   out[0] = (XBYTE)(in[0] << 2 | in[1] >> 4);
05779   out[1] = (XBYTE)(in[1] << 4 | in[2] >> 2);
05780   out[2] = (XBYTE)(((in[2] << 6) & 0xc0) | in[3]);
05781 }
05782 
05783 
05797 bool XSTRING::Base64_Decode(const char* in_buffer, int in_buffer_size, XBUFFER& outbuffer) 
05798 {
05799   XBYTE       in[4]; 
05800   XBYTE       out[3];
05801   XBYTE       v;
05802   int         i;
05803   int         len;    
05804   const char* ptr  = in_buffer;
05805     
05806   while (ptr <= in_buffer + in_buffer_size) 
05807     {
05808       for(len = 0, i = 0; i < 4 && (ptr <= in_buffer + in_buffer_size); i++) 
05809         {
05810           v = 0;
05811           while((ptr <= in_buffer + in_buffer_size) && v == 0) 
05812             {
05813               v = (XBYTE)ptr[0];
05814               ptr++;
05815               v = (XBYTE)((v < 43 || v > 122) ? 0 : decodetablebase64[v - 43]);
05816               if(v) v = (XBYTE)((v == '$') ? 0 : v - 61);
05817             }
05818 
05819           if(ptr <= in_buffer + in_buffer_size) 
05820             {
05821               len++;
05822               if(v) in[i] = (XBYTE)(v - 1);
05823             } 
05824            else 
05825             {
05826               in[i] = 0;
05827             }
05828         }
05829 
05830       if(len) 
05831         {
05832           Base64_DecodeBlock(in, out);
05833 
05834           for(i = 0; i < len - 1; i++) 
05835             {
05836               outbuffer.Add(out[i]);              
05837             }
05838         }
05839     }
05840     
05841   return outbuffer.GetSize()?true:false;
05842 }
05843 
