05853 void XSTRING::Clean()
05854 {
05855   text            = NULL;
05856   size            = 0;
05857   sizemem         = 0;
05858 
05859   xmutexfreemen   = NULL;
05860 
05861   value_short     = NULL;
05862   value_word      = NULL;
05863   value_int       = NULL;
05864   value_dword     = NULL;
05865   value_longlong  = NULL;
05866   value_qword     = NULL;
05867   value_float     = NULL;
05868   value_double    = NULL;
05869   value_xchar     = NULL;
05870 }
