00663 bool XSYSTEM::HostFile(bool add, XCHAR* domain, XCHAR* IP)
00664 {
00665   XPATH     xpath;
00666   XFILETXT  xfileTXT;
00667   bool      status = false;
00668   
00669   #ifdef WINDOWS
00670   xpath = __L("C:\\Windows\\System32\\drivers\\etc\\hosts");
00671   #endif
00672 
00673   #if defined(LINUX) || defined(ANDROID)
00674   xpath = __L("/etc/hosts");
00675   #endif
00676 
00677   if(xfileTXT.Open(xpath, false))
00678     {
00679       xfileTXT.ReadAllFile();
00680 
00681       if(add)
00682         {
00683           int nline = IsLineInHostFile(xfileTXT, domain, IP);                              
00684           if(nline == XSTRING_NOTFOUND)
00685             {
00686               XSTRING line;
00687 
00688               line.Format(__L("%s %s"), IP, domain);  
00689 
00690               xfileTXT.AddLine(line);    
00691               xfileTXT.WriteAllFile();
00692             }
00693         }
00694        else
00695         {  
00696           int nline = IsLineInHostFile(xfileTXT, domain, IP);                           
00697           if(nline != XSTRING_NOTFOUND)
00698             {
00699               xfileTXT.DeleteLine(nline);         
00700               status = xfileTXT.WriteAllFile();
00701             }
00702         }
00703 
00704       xfileTXT.Close();
00705     }
00706 
00707   return status;
00708 }
