00743 int XSYSTEM::IsLineInHostFile(XFILETXT& xfileTXT, XCHAR* domain, XCHAR* IP)
00744 {
00745   bool found = false;    
00746   int  c     = 0;
00747 
00748   if(!xfileTXT.GetNLines()) return XSTRING_NOTFOUND;
00749 
00750   do{ XSTRING* line = xfileTXT.GetLine(c); 
00751       if(line)
00752         {
00753           int index = line->Find(domain, true);  
00754           if(index != XSTRING_NOTFOUND) 
00755             {     
00756               found = true;
00757 
00758               for(XDWORD d=0; d<line->GetSize(); d++)
00759                 {
00760                   XCHAR character = line->Get()[d];
00761 
00762                   if((character != __C('\t') && (character != __C(' '))))     
00763                     {
00764                       if(character == __C('#'))     
00765                         {
00766                           found = false;
00767                         }
00768 
00769                       break;
00770                     }               
00771                 }
00772                       
00773               if(found) break; 
00774             }
00775         }
00776 
00777       c++;
00778 
00779     } while(c<xfileTXT.GetNLines());
00780 
00781   if(!found) return XSTRING_NOTFOUND;
00782 
00783   return c;
00784 }
