01464           string += __L(" ");
01465 
01466           for(XDWORD c=0; c<_sizeline; c++)
01467             {
01468               XCHAR character = (XCHAR)data[index];
01469 
01470               if((character<0x20) || (character>0x80) || (character==__C('%')) || (character==__C('\\'))) character = __C('.');
01471 
01472               strdata.Format(__L("%c"),character);
01473               string += strdata;
01474               index++;
01475             }
01476         }
01477 
01478       Print(level, string.Get());
01479 
01480       if(_size>XTRACE_MAXSIZEDATABLOCK) break;
01481     }
01482 
01483   return true;
01484 }
