01775 XDWORD XTRACE::GetTraceFromXBuffer(XBUFFER& xbufferpacket, XDWORD& publicIP, XDWORD& localIP, XBYTE& level, XDWORD& sequence, XDATETIME* xtime, XBUFFER& data)
01776 {
01777   if(!xtime)                   return 1;
01778   if(!xbufferpacket.GetSize()) return 1;
01779 
01780   XDWORD IDpacket = XTRACE_IDPACKET;
01781   
01782   XDWORD    sizepacketread  = 0;
01783   XDWORD    dword;
01784   XDWORD    sizeinbuffer    = xbufferpacket.GetSize();
01785   XBUFFER   xbuffer(false);
01786   XDWORD    sizeheader      = (sizeof(XDWORD)    +    // ID.
01787                                sizeof(XDWORD)    +    // SizePacket.
01788                                sizeof(XDWORD)    +    // public IP address origin.
01789                                sizeof(XDWORD)    +    // local  IP address origin.
01790                                sizeof(XBYTE)     +    // Level.
01791                                sizeof(XDWORD)    +    // Sequence.
01792                               (sizeof(XDWORD)*7) +    // Date Time.
01793                                sizeof(XWORD));        // CRC16
01794 
01795   if(!sizeinbuffer)             return 1;
01796   if(sizeinbuffer < sizeheader) return 1;
01797 
01798 
01799   while(sizeinbuffer-4)
01800     {
01801       xbufferpacket.Get(IDpacket, 0);
01802       if(IDpacket == XTRACE_IDPACKET) break;
01803      
01804       xbufferpacket.Extract(NULL, 0, 1);
01805       sizeinbuffer--;
01806     }
01807 
01808   if(IDpacket != XTRACE_IDPACKET) return 1;
01809 
01810   if(!sizeinbuffer)               return 1;
01811   if(sizeinbuffer < sizeheader)   return 1;
01812 
01813   if(!xbufferpacket.Get(sizepacketread, sizeof(IDpacket)))
01814     {
01815       return 1;
01816     }
01817 
01818   if(!sizepacketread)
01819     {
01820       return 2;
01821     }
01822 
01823   if(sizepacketread > sizeinbuffer)
01824     {
01825       return 1;
01826     }
01827 
01828  
01829   XDWORD sizeassing    = sizepacketread;
01830   XDWORD sizeassingmin = 5*1024;
01831 
01832   if(sizeassing < sizeassingmin) sizeassing = sizeassingmin;
01833   xbuffer.Resize(sizeassing);
01834 
01835   xbufferpacket.Extract(xbuffer.Get(), 0, sizepacketread);
01836 
01837   XWORD crc16;
01838   XTRACE_CRC16(xbuffer.Get(), (xbuffer.GetSize()-sizeof(XWORD)), crc16)
01839 
01840   XWORD crc16read;
01841 
01842   xbuffer.Get(crc16read, xbuffer.GetSize()-sizeof(XWORD));
01843   if(crc16 != crc16read)
01844     {
01845       return 2;
01846     }
01847 
01848   xbuffer.SetPosition(0);
01849 
01850   xbuffer.Get(IDpacket);
01851   xbuffer.Get(sizepacketread);
01852   xbuffer.Get(publicIP);
01853   xbuffer.Get(localIP);
01854   xbuffer.Get(level);
01855   xbuffer.Get(sequence);
01856 
01857   xbuffer.Get(dword); xtime->SetYear(dword);
01858   xbuffer.Get(dword); xtime->SetMonth(dword);
01859   xbuffer.Get(dword); xtime->SetDay(dword);
01860   xbuffer.Get(dword); xtime->SetHours(dword);
01861   xbuffer.Get(dword); xtime->SetMinutes(dword);
01862   xbuffer.Get(dword); xtime->SetSeconds(dword);
01863   xbuffer.Get(dword); xtime->SetMilliSeconds(dword);
01864 
01865   XDWORD sizestr = sizepacketread - sizeheader;        // CRC16  
01866   data.Empty();
01867   data.Add(&xbuffer.Get()[xbuffer.GetPosition()], sizestr);
01868 
01869 
01870   return 0;
01871 }
