01947 bool XTRACE::SetTraceToXBuffer(XDWORD publicIP, XDWORD localIP, XBYTE level, XDWORD sequence, XDATETIME* xtime, XDWORD& sizepacket, XBUFFER& xbufferpacket)
01948 {
01949   if(!xtime)  return false;
01950     
01951   xbufferpacket.Empty();
01952 
01953   XDWORD IDpacket = XTRACE_IDPACKET;
01954  
01955   sizepacket += sizeof(XDWORD) +                        // ID.
01956                 sizeof(XDWORD) +                        // SizePacket.
01957                 sizeof(XDWORD) +                        // public IP address origin.
01958                 sizeof(XDWORD) +                        // local  IP address origin.
01959                 sizeof(XBYTE)  +                        // Level.
01960                 sizeof(XDWORD) +                        // Sequence.
01961                 (sizeof(XDWORD)*7) +                    // Date Time.                
01962                 sizeof(XWORD);                          // CRC16
01963 
01964   xbufferpacket.Resize(sizepacket);
01965 
01966   xbufferpacket.Set(IDpacket);  
01967   xbufferpacket.Set(sizepacket);
01968   xbufferpacket.Set(publicIP);
01969   xbufferpacket.Set(localIP);
01970   xbufferpacket.Set(level);
01971   xbufferpacket.Set(sequence);
01972 
01973   xbufferpacket.Set((XDWORD)xtime->GetYear());
01974   xbufferpacket.Set((XDWORD)xtime->GetMonth());
01975   xbufferpacket.Set((XDWORD)xtime->GetDay());
01976   xbufferpacket.Set((XDWORD)xtime->GetHours());
01977   xbufferpacket.Set((XDWORD)xtime->GetMinutes());
01978   xbufferpacket.Set((XDWORD)xtime->GetSeconds());
01979   xbufferpacket.Set((XDWORD)xtime->GetMilliSeconds());
01980 
01981   return true;  
01982 }
