02002 bool XTRACE::SetTraceTextToXBuffer(XDWORD publicIP, XDWORD localIP, XBYTE level, XDWORD sequence, XDATETIME* xtime, XCHAR* string, XBUFFER& xbufferpacket)
02003 {     
02004   if(!string) return false;
02005   
02006   XSTRING  line;
02007   XBUFFER  bufferline;
02008 
02009   line = string;  
02010   line.ConvertToExchangeXBuffer(bufferline);
02011   
02012   XDWORD sizepacket = bufferline.GetSize();    // Line (WORD) each character.
02013   
02014   if(!SetTraceToXBuffer(publicIP, localIP, level, sequence, xtime, sizepacket, xbufferpacket)) return false;
02015   
02016   xbufferpacket.Set(bufferline.Get(), bufferline.GetSize());
02017   
02018   XWORD crc16;
02019   XTRACE_CRC16(xbufferpacket.Get(), (xbufferpacket.GetSize()-sizeof(XWORD)), crc16)
02020 
02021   xbufferpacket.Set((XWORD)crc16);
02022     
02023   return true; 
02024 }
