02041 
02051 bool XTRACE::ObtainLocalIP()
02052 {
02053   bool status = false;
02054 
02055   #if(defined(DIO_ACTIVE) && defined(DIO_STREAMUDP_ACTIVE))
02056 
02057   DIOSTREAMIPLOCALENUMDEVICES* enumdevices = (DIOSTREAMIPLOCALENUMDEVICES*)GEN_DIOFACTORY.CreateStreamEnumDevices(DIOSTREAMENUMTYPE_IP_LOCAL);
02058   if(enumdevices)
02059     {
02060       if(!localIPfilter)
02061         {
02062           DIOSTREAMDEVICEIP* device = (DIOSTREAMDEVICEIP*)enumdevices->GetFirstActiveDevice();
02063           if(device)
02064             {
02065               XSTRING string;
02066 
02067               device->GetIP()->GetXString(string);
02068               SetLocalIPString(string.Get());
02069 
02070               status = true;
02071             }
02072         }
02073        else
02074         {
02075           enumdevices->Search();
02076 
02077           for(XDWORD c=0; c<enumdevices->GetDevices()->GetSize(); c++)
02078             {
02079               DIOSTREAMDEVICEIP* device = (DIOSTREAMDEVICEIP*)enumdevices->GetDevices()->Get(c);
02080               if(device)
02081                 {
02082                   //device->DebugPrintInfo();
02083 
02084                   if((!device->GetIP()->IsEmpty()) &&
02085                      ((device->GetIPType() == DIOSTREAMIPDEVICE_TYPE_ETHERNET) || (device->GetIPType() == DIOSTREAMIPDEVICE_TYPE_WIFI) || (device->GetIPType() == DIOSTREAMIPDEVICE_TYPE_PPP)) &&
02086                       (device->GetIP()->Get()[0] == localIPfilter))
02087                     {
02088                       XSTRING string;
02089 
02090                       device->GetIP()->GetXString(string);
02091                       SetLocalIPString(string.Get());
02092                       status = true;
02093                     }
02094                 }
02095             }
02096         }
02097 
02098       GEN_DIOFACTORY.DeleteStreamEnumDevices(enumdevices);
02099     }
02100 
02101   #endif
02102 
02103   return status;
02104 }
