02118 bool XTRACE::SetLocalIPString(XCHAR* localIPstring)
02119 {
02120   XSTRING _localIPstring;
02121   int data[4];
02122 
02123   _localIPstring = localIPstring;
02124   _localIPstring.UnFormat(__L("%d.%d.%d.%d"), &data[0], &data[1], &data[2], &data[3]);
02125 
02126   localIP  |= (data[0] << 24);
02127   localIP  |= (data[1] << 16);
02128   localIP  |= (data[2] << 8);
02129   localIP  |= (data[3]);
02130 
02131   return true;
02132 }
