02329 bool XTRACE::ResolveAllNetTargets(bool& changed)
02330 {
02331   changed = false;
02332 
02333   for(int c=0; c<XTRACE_MAXNTARGETS; c++)
02334     {
02335       if(targets[c].GetType() == XTRACE_TYPE_NET)
02336         {
02337           #if(defined(DIO_ACTIVE) && defined(DIO_STREAMUDP_ACTIVE))
02338 
02339           XSTRING IP;
02340 
02341           IP = targets[c].GetIP();
02342 
02343           targets[c].IPTarget();
02344 
02345           if(IP.Compare(targets[c].GetIP())) changed = true;
02346 
02347           #endif
02348         }
02349     }
02350 
02351   return true;
02352 }
