02364 bool XTRACE::ResolveAllResources()
02365 {
02366   bool changed;
02367 
02368   ResolveAllNetTargets(changed);
02369   if(changed)
02370     {
02371       ObtainResourcesIP();
02372       ReConnectedAllNetTargets();
02373     }
02374 
02375   return true;
02376 }
