00241 bool XTRANSLATION_GEN::Sentence_Add(XDWORD ID, XDWORD codelanguage, XCHAR* sentence, XDWORD fixed)
00242 {
00243   if(!ID)           return false;
00244   if(!codelanguage) return false;
00245   if(!sentence)     return false;
00246 
00247   if(Sentence_Get(ID, codelanguage)) return false;
00248 
00249   XTRANSLATION_GEN_SENTENCE* sentenceGEN = new XTRANSLATION_GEN_SENTENCE();
00250   if(!sentenceGEN) return false;
00251 
00252   int sizesentence = XSTRING::GetSize(sentence);
00253 
00254   sentenceGEN->ID           = ID;
00255   sentenceGEN->codelanguage = codelanguage;
00256   sentenceGEN->sentence     = new XCHAR[sizesentence+1];
00257   sentenceGEN->fixed        = fixed;
00258   if(!sentenceGEN->sentence)
00259     {
00260       delete sentenceGEN;
00261       return false;
00262     }
00263 
00264   memset(sentenceGEN->sentence,        0, (sizesentence+1) * sizeof(XCHAR));
00265   memcpy(sentenceGEN->sentence, sentence, sizesentence     * sizeof(XCHAR));
00266 
00267   sentences.Add(sentenceGEN);
00268 
00269   return true;
00270 }
