00285 XTRANSLATION_GEN_SENTENCE* XTRANSLATION_GEN::Sentence_Get(XDWORD ID, XDWORD codelanguage)
00286 {
00287   for(XDWORD c=0; c<sentences.GetSize(); c++)
00288     {
00289       XTRANSLATION_GEN_SENTENCE* sentence = sentences.Get(c);
00290       if(sentence)
00291         {
00292           if((sentence->ID == ID) && (sentence->codelanguage == codelanguage)) return sentence;
00293         }
00294     }
00295 
00296   return NULL;
00297 }
