00460 bool XTRANSLATION_GEN::Sentences_AddToTranslation(XDWORD codelanguage)
00461 {
00462   bool existsentenceGENlanguage = false;
00463 
00464   for(XDWORD c=0; c<sentences.GetSize(); c++)
00465     {
00466       XTRANSLATION_GEN_SENTENCE* sentenceGEN = sentences.Get(c);
00467       if(sentenceGEN)
00468         {
00469           if(sentenceGEN->codelanguage == codelanguage)
00470             {
00471               GEN_XTRANSLATION.Translate_Add(sentenceGEN->ID, sentenceGEN->sentence, sentenceGEN->fixed);
00472               existsentenceGENlanguage = true;
00473             }
00474         }
00475     }
00476 
00477   return existsentenceGENlanguage;
00478 }
