00098 bool XWINDOWSDRIVEIMAGEMANAGER::GetDrives(XVECTOR<XDRIVEIMAGEMANAGER_DRIVE*>& drives)
00099 {
00100   drives.DeleteContents();
00101   drives.DeleteAll();
00102 
00103   XPATH   path;
00104   XDWORD  drivemask = GetLogicalDrives();
00105   XQWORD  ID        = 0;
00106   int     c         = 0;
00107 
00108   // GetLogicalDrives returns 0 on failure, or a bitmask representing the drives available on the system (bit 0 = A:, bit 1 = B:, etc)
00109 
00110   while(drivemask != 0)
00111     {
00112       if(drivemask & 1)
00113         {
00114           // the "A" in path will get incremented by the # of bits
00115           // we've shifted
00116           path.Format( __L("\\\\.\\%c:\\"), __C('A') + c);
00117           if(CheckDriveType(path, ID))
00118             {
00119               XDRIVEIMAGEMANAGER_DRIVE* drive = new XDRIVEIMAGEMANAGER_DRIVE();
00120               if(drive)
00121                 {
00122                   XSTRING label;
00123 
00124                   drive->GetName()->Format(__L("%c:"), __C('A') + c);
00125                   drive->GetPath()->Set(path);
00126                   drive->SetVolume(c);
00127                   drive->GetLabel()->Set(label);
00128                   drive->SetID(ID);
00129 
00130                   if(GetDriveLabel(drive->GetName()->Get(), label))  drive->GetLabel()->Set(label);
00131 
00132                   drives.Add(drive);
00133                 }
00134             }
00135         }
00136 
00137       drivemask >>= 1;
00138 
00139       ++c;
00140     }
00141 
00142   return true;
00143 }
