00157 bool XWINDOWSDRIVEIMAGEMANAGER::Open(XDRIVEIMAGEMANAGER_DRIVE& drive)
00158 {
00159   HANDLE handlevolume = GetHandleFromVolume(drive.GetVolume(), GENERIC_READ | GENERIC_WRITE);
00160   if(handlevolume == INVALID_HANDLE_VALUE) return false;
00161 
00162   drive.SetHandle((XDWORD)handlevolume);
00163 
00164   XDWORD deviceID = 0;
00165   if(!GetDeviceID(handlevolume, deviceID)) return false;
00166   drive.SetDeviceID(deviceID);
00167 
00168   if(!LockVolume(handlevolume, true))  return false;
00169 
00170   if(!UnMountVolume(handlevolume))    return false;
00171 
00172   HANDLE rawhandle = GetHandleFromDevice(deviceID, GENERIC_READ | GENERIC_WRITE);
00173   if(rawhandle == INVALID_HANDLE_VALUE) return false;
00174 
00175   drive.SetRawHandle((XDWORD)rawhandle);
00176 
00177   XQWORD sectorsize    = 0;
00178   XQWORD numbersectors = 0;
00179 
00180   if(!GetNumberOfSectors(rawhandle, sectorsize, numbersectors)) return false;
00181 
00182   drive.SetSectorSize(sectorsize);
00183   drive.SetNumberSectors(numbersectors);
00184 
00185   return true;
00186 }
