00203 bool XWINDOWSDRIVEIMAGEMANAGER::Read(XDRIVEIMAGEMANAGER_DRIVE& drive, XQWORD startsector, XQWORD numbersectors, XBUFFER& buffer)
00204 {
00205   XBYTE* _buffer;
00206   XDWORD maxbuffer         = ((XDWORD)numbersectors * (XDWORD)drive.GetSectorSize());
00207   XQWORD maxnsectorsblock  = (maxbuffer / drive.GetSectorSize());
00208   XQWORD readnsectorsblock = 0;
00209   XQWORD sizeread          = 0;
00210   bool   status            = false;
00211 
00212   buffer.Delete();
00213 
00214   _buffer = new XBYTE[maxbuffer];
00215   if(!_buffer) return false;
00216 
00217   if(maxnsectorsblock > numbersectors) maxnsectorsblock = numbersectors;
00218 
00219   while(readnsectorsblock < numbersectors)
00220     {
00221       XDRIVEIMAGEMANAGER_XEVENT xevent(this, XDRIVEIMAGEMANAGER_XEVENT_TYPE_READSECTORS);
00222 
00223       xevent.SetDrive(&drive);
00224       xevent.SetIndexSector(startsector);
00225       xevent.SetNumberSectorsInOperation(maxnsectorsblock);
00226 
00227       sizeread = (maxnsectorsblock * drive.GetSectorSize());
00228 
00229       memset(_buffer, 0, maxbuffer);
00230 
00231       status = ReadSectorDataFromHandle((HANDLE)drive.GetRawHandle(), startsector, drive.GetSectorSize(), _buffer, sizeread);
00232       if(status)
00233         {
00234           xevent.SetError(XDRIVEIMAGEMANAGER_ERROR_NONE);
00235 
00236           buffer.Add(_buffer, (XDWORD)sizeread);
00237 
00238           readnsectorsblock += maxnsectorsblock;
00239 
00240           if((readnsectorsblock + maxnsectorsblock) > numbersectors)             maxnsectorsblock = (numbersectors - readnsectorsblock);
00241           if((readnsectorsblock + maxnsectorsblock) > drive.GetNumberSectors())  maxnsectorsblock = (drive.GetNumberSectors() - readnsectorsblock);
00242         }
00243        else
00244         {
00245           xevent.SetError(XDRIVEIMAGEMANAGER_ERROR_READSECTORS);
00246         }
00247 
00248       PostEvent(&xevent);
00249 
00250       if(!status) break;
00251 
00252     }
00253 
00254   delete [] _buffer;
00255 
00256   return status;
00257 }
